    <span class="token annotation punctuation">@Nullable</span> <span class="token class-name">ReadableArray</span> addAtIndices<span class="token punctuation">,</span>
    <span class="token annotation punctuation">@Nullable</span> <span class="token class-name">ReadableArray</span> removeFrom<span class="token punctuation">)</span> <span class="token punctuation">{</span><span class="token punctuation">.</span><span class="token punctuation">.</span><span class="token punctuation">.</span><span class="token punctuation">}</span>
    mUIImplementation<span class="token punctuation">.</span><span class="token function">manageChildren</span><span class="token punctuation">(</span>
          viewTag<span class="token punctuation">,</span> moveFrom<span class="token punctuation">,</span> moveTo<span class="token punctuation">,</span> addChildTags<span class="token punctuation">,</span> addAtIndices<span class="token punctuation">,</span> removeFrom<span class="token punctuation">)</span><span class="token punctuation">;</span>
